import { Fragment, useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import {
  Dialog,
  Disclosure,
  Popover,
  RadioGroup,
  Tab,
  Transition,
} from "@headlessui/react";
import {
  Bars3Icon,
  HeartIcon,
  MagnifyingGlassIcon,
  MinusIcon,
  PlusIcon,
  ShoppingBagIcon,
  UserIcon,
  XMarkIcon,
} from "@heroicons/react/24/outline";
import { StarIcon } from "@heroicons/react/20/solid";
import HeaderNav from "../components/HeaderNav";
import navigation from "../components/navigationData";
// import product from "../components/productData";
import relatedProducts from "../components/relatedProductData";
import FooterComponent from "../components/FooterComponent";
import footerNavigation from "../components/footerNavigation";
import AlsoBought from "../components/AlsoBought";

// {确保后端API正确响应并返回所需数据。
//   在实际部署中，API URL 应使用环境变量或配置文件管理，不要硬编码。
//   适当处理加载和错误状态，提高用户体验。}
function classNames(...classes) {
  return classes.filter(Boolean).join(" ");
}

export default function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // 当组件加载时获取产品数据
    const fetchProductData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/product/${id}`);
        setProduct(response.data); // 设置产品数据
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    };

    fetchProductData();
  }, [id]); // 当id变化时，重新获取数据

  if (!product) {
    return <div> Loading... </div>;
  }

  return (
    <div className="bg-white">
      {/* 省略其他部分 */}
      <main className="mx-auto max-w-7xl sm:px-6 sm:pt-16 lg:px-8">
        <div className="mx-auto max-w-2xl lg:max-w-none">
          {/* Product info */}
          <div className="lg:grid lg:grid-cols-2 lg:items-start lg:gap-x-8">
            {/* Image gallery */}
            {/* ... 省略图片展示部分 */}
            {/* Product info */}
            <div className="mt-10 px-4 sm:mt-16 sm:px-0 lg:mt-0">
              <h1 className="text-3xl font-bold tracking-tight text-gray-900">
                {product.name}
              </h1>
              <p className="text-3xl tracking-tight text-gray-900">
                {product.price}
              </p>
              {/* 省略其他部分 */}
            </div>
          </div>
        </div>
      </main>
      {/* 省略其他部分 */}
    </div>
  );
}
