import { Fragment, useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import {
  Dialog,
  Disclosure,
  Popover,
  RadioGroup,
  Tab,
  Transition,
} from "@headlessui/react";
import {
  Bars3Icon,
  HeartIcon,
  MagnifyingGlassIcon,
  MinusIcon,
  PlusIcon,
  ShoppingBagIcon,
  UserIcon,
  XMarkIcon,
} from "@heroicons/react/24/outline";
import { StarIcon } from "@heroicons/react/20/solid";
import HeaderNav from "../components/HeaderNav";
import navigation from "../components/navigationData";
import product from "../components/productData";
import relatedProducts from "../components/relatedProductData";
import FooterComponent from "../components/FooterComponent";
import footerNavigation from "../components/footerNavigation";
import AlsoBought from "../components/AlsoBought";

function classNames(...classes) {
  return classes.filter(Boolean).join(" ");
}

export default function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // 当组件加载时获取产品数据
    const fetchProductData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/products/${id}`
        );
        setProduct(response.data); // 设置产品数据
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    };

    fetchProductData();
  }, [id]); // 当id变化时，重新获取数据

  if (!product) {
    return <div> Loading... </div>;
  }
}
